package basics;

public class Pattern7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i=1;i<=6;i++) 
		{
			for(int col=1;col<=i;col++)
			{
				System.out.print("*");
			}
			for(int colnum=1;colnum<=5;colnum++)
			{
				System.out.print(colnum);
			}
			System.out.println();
		
			  
		}
		
	}
}
